@import url("https://fonts.googleapis.com/css?family=Abel");
html,
body,
div,
span,
applet,
object,
iframe,
h1,
h2,
h3,
h4,
h5,
h6,
p,
blockquote,
pre,
a,
abbr,
acronym,
address,
big,
cite,
code,
del,
dfn,
em,
img,
ins,
kbd,
q,
s,
samp,
small,
strike,
strong,
sub,
sup,
tt,
var,
b,
u,
i,
center,
dl,
dt,
dd,
ol,
ul,
li,
fieldset,
form,
label,
legend,
table,
caption,
tbody,
tfoot,
thead,
tr,
th,
td,
article,
aside,
canvas,
details,
embed,
figure,
figcaption,
footer,
header,
hgroup,
menu,
nav,
output,
ruby,
section,
summary,
time,
mark,
audio,
video {
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  font: inherit;
  vertical-align: baseline;
}

/* HTML5 display-role reset for older browsers */
article,
aside,
details,
figcaption,
figure,
footer,
header,
hgroup,
menu,
nav,
section {
  display: block;
}

body {
  line-height: 1;
}

ol,
ul {
  list-style: none;
}

blockquote,
q {
  quotes: none;
}

blockquote:before,
blockquote:after,
q:before,
q:after {
  content: "";
  content: none;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

::selection {
  color: #d2d2d2;
}

@keyframes appear__left {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@font-face {
  font-family: "Manifesto";
  src: url("afcf595159a73ae75657c7b2f579f5bd.ttf");
}
html {
  cursor: url(e0f47cf0a1fe74a9cbef128408143b09.png) 8 8, auto;
}

body {
  background: #191b22;
  background-repeat: repeat;
  font-family: "Abel", sans-serif;
  transition: background-color 300ms;
}

a {
  cursor: url(e0f47cf0a1fe74a9cbef128408143b09.png) 11 11, auto;
}

header {
  width: 100vw;
  display: flex;
  justify-content: flex-end;
  height: 50px;
}

.header__social {
  margin-right: 25px;
  display: flex;
  margin-top: 30px;
}
.header__social img:not(.first) {
  margin-left: 15px;
}

.home {
  display: flex;
  width: 100vw;
  height: calc(100vh - 150px);
  justify-content: center;
  align-items: center;
  flex-direction: column;
}
.home .home__bigTitle {
  color: white;
  text-transform: uppercase;
  font-family: Manifesto;
  font-size: 30px;
}
@media screen and (min-width: 980px) {
  .home .home__bigTitle {
    font-size: 40px;
  }
}
.home .home__presentation {
  color: #525356;
  width: 250px;
  text-align: center;
  margin-top: 15px;
  line-height: 120%;
}
@media screen and (min-width: 980px) {
  .home .home__presentation {
    width: 500px;
    font-size: 18px;
  }
}
.home .home__specs {
  margin-top: 5px;
  color: #525356;
  font-size: 15px;
}
@media screen and (min-width: 980px) {
  .home .home__specs {
    font-size: 17px;
    animation: appear__left 5s;
  }
}
.home .home__bigTitle {
  animation: appear__left 2s;
}

.picture--animated {
  animation: picture__one 2s;
}

.picture--two {
  animation: picture__two 2s;
}

.picture--three {
  animation: picture__three 2s;
}

@keyframes picture__one {
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
}
@keyframes picture__two {
  from {
    transform: translateX(100%);
  }
  to {
    transform: translateX(0);
  }
}
@keyframes picture__three {
  from {
    transform: translateY(100%);
  }
  to {
    transform: translateX(0);
  }
}
.projets {
  margin-top: 10px;
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}
@media screen and (min-width: 980px) {
  .projets {
    display: grid;
    grid-template-columns: 2fr 1fr 1fr 1fr 1fr;
    grid-template-rows: 1fr 1fr 1fr 1fr 1fr 1fr;
    grid-gap: 10px;
  }
}

.picture--one {
  background-image: url(b6885c99f0262163c57bb6df470d2dc1.png);
  animation: picture__one 2s;
}

.picture--two {
  background-image: url(038d637f5b3bf2801a11f974671ae565.png);
}

.picture--three {
  background: url(e5330116aad0f14dc6a6bd5a2febe4c0.png);
}

.picture {
  filter: brightness(60%);
  cursor: url(779cdc0fea188859cde4bcf2e0a653a4.png), auto;
  position: relative;
  width: calc(100% - 30px);
  background-size: cover;
  height: 540px;
  background-repeat: no-repeat;
  box-shadow: 0px 0px 30px rgba(0, 0, 0, 0.3);
  margin-bottom: 40px;
}
@media screen and (min-width: 980px) {
  .picture {
    margin-left: 30px;
    grid-column: 1/span 2;
    grid-row: 1/span 5;
  }
}

@media screen and (min-width: 980px) {
  .picture--two {
    height: 320px;
    width: 314px;
    grid-column: 3/span 2;
    grid-row: 2/span 2;
  }

  .picture--three {
    height: 320px;
    width: 314px;
    grid-column: 4/span 2;
    grid-row: 3/span 2;
  }

  .projets__title {
    bottom: -30px;
  }
}
.projets__title {
  color: #6a6a6a;
  position: absolute;
  font-size: 16px;
  bottom: -25px;
  left: 5px;
}

.projets__all {
  margin: 40px auto;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #e9e9e9;
  height: 40px;
  width: 160px;
  border-radius: 100px;
  color: black;
  box-shadow: black 0 0 10px;
  transition: 2s;
}

@keyframes appear {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@keyframes line {
  from {
    width: 0;
  }
  to {
    width: 70px;
  }
}
@keyframes specs {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}
@keyframes get {
  from {
    transform: translate(-100%);
  }
  to {
    transform: translate(0);
  }
}
.logo a {
  font-family: MANIFESTO;
  line-height: normal;
  font-size: 15px;
  color: #eeefef;
  margin-top: 30px;
  margin-left: 10px;
  text-decoration: none;
  display: block;
  animation: appear 1400ms;
}
@media screen and (min-width: 980px) {
  .logo a {
    margin-left: 30px;
  }
}

.header--special {
  justify-content: space-between;
}

.container__projets {
  position: relative;
  margin: 30px 10px 30px 10px;
  height: auto;
}
@media screen and (min-width: 980px) {
  .container__projets {
    margin: 30px;
  }
}

.next a {
  position: absolute;
  margin-top: 20px;
  right: 0;
  margin-bottom: 50px;
  color: white;
  font-family: Manifesto;
  font-size: 14px;
}
@media screen and (min-width: 980px) {
  .next a {
    font-size: 17px;
  }
}

.previous a {
  position: absolute;
  margin-bottom: 50px;
  left: 0;
  margin-top: 20px;
  color: white;
  font-family: Manifesto;
  font-size: 13px;
}
@media screen and (min-width: 980px) {
  .previous a {
    font-size: 17px;
  }
}

.title__project {
  color: white;
  font-size: 23px;
  font-family: Manifesto;
}

.text__project {
  line-height: 112%;
  color: #525356;
  font-size: 18px;
  margin-top: 10px;
  margin-bottom: 20px;
}
@media screen and (min-width: 980px) {
  .text__project {
    width: 700px;
  }
}

.role {
  margin-top: 30px;
  font-family: garamond-premier-pro-display, serif;
  font-weight: 400;
  font-style: normal;
  color: white;
  font-size: 18px;
}

.line {
  height: 1px;
  width: 70px;
  background-color: white;
  display: inline-block;
  transform: translateY(-4px);
  animation: line 1s;
}

.role--specs {
  animation: specs 2s;
}

.project {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-top: 20px;
}
.project img {
  width: 60%;
  border-radius: 7px;
  box-shadow: rgba(0, 0, 0, 0.466) 0 0 10px;
  animation: get 2s;
}

.project--noShadow img {
  box-shadow: 0 0 0;
}

.link_project a {
  color: rgba(255, 255, 255, 0.37);
  font-size: 13px;
  display: block;
}

.contact__content {
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100vh;
  flex-direction: column;
}
.contact__content img {
  height: 40px;
}

h2 {
  font-size: 34px;
  margin-bottom: 10px;
  text-transform: uppercase;
}
@media screen and (min-width: 980px) {
  h2 {
    font-size: 54px;
  }
}

a {
  text-decoration: none;
  color: black;
}
